# --- USER MANAGEMENT ---
def ensure_user_sync(user_id, username):
    conn_local = get_connection()
    cur = conn_local.cursor()
    try:
        cur.execute("SELECT 1 FROM users WHERE user_id=%s", (user_id,))
        if not cur.fetchone():
            cur.execute(
                "INSERT INTO users (user_id, username) VALUES (%s, %s)",
                (user_id, username),
            )
        conn_local.commit()
    finally:
        cur.close()
        conn_local.close()

async def ensure_user(user_id, username):
    await asyncio.to_thread(ensure_user_sync, user_id, username)

def update_score(user_id: int, correct: bool):
    conn_local = get_connection()
    cur = conn_local.cursor()
    try:
        if correct:
            cur.execute("UPDATE users SET wins = wins + 1 WHERE user_id=%s", (user_id,))
        else:
            cur.execute("UPDATE users SET losses = losses + 1 WHERE user_id=%s", (user_id,))
        conn_local.commit()
    finally:
        cur.close()
        conn_local.close()

